using Newtonsoft.Json;
using Newtonsoft.Json.Linq;
using SynCraftClient.Models;
using System;
using System.Collections.Generic;
using System.IO;
using System.IO.Compression;

namespace SynCraftClient.Controllers
{
	internal class SessionController
	{
		private readonly Session SessionModel = new();

		public SessionController()
		{
            SessionModel.ServerManager = new ServerController();
		}

		public void Start()
		{
			try { SessionModel.ServerManager.Connect(); }
			catch (Exception ex)
			{ 
				LogsController.Error("Error while connecting to the server: " + ex.Message);
				return;
			}

			if (Settings.SyncMods)
				RecieveMods();
			if (Settings.SyncConfig)
                RecieveConfig();
			if (Settings.SyncForge)
				RecieveForge();
			if (Settings.SyncShaders)
                RecieveShaders();
            if (Settings.SyncKeymap)
                RecieveKeymap();

            LogsController.Info("Synchronization completed");
            SessionModel.ServerManager.SendString("Disconnect");
            SessionModel.ServerManager.Disconnect();
		}

		private void RecieveMods()
		{
            SessionModel.ServerManager.SendString("GetMods");
            SessionModel.ServerManager.RecvString(out string buff);

			if (buff != "OK")
			{
				LogsController.Info("Skipping mods synchronization, disabled by the server");
				return;
			}

            string modsPath = Settings.MinecraftPath + "\\mods";

            if (Directory.Exists(modsPath))
				Directory.Delete(modsPath, true);

			Directory.CreateDirectory(modsPath);

			while (true)
			{
                SessionModel.ServerManager.RecvString(out buff);

				if (buff == "END")
					break;

				LogsController.Info("Downloading mod " + buff);

                SessionModel.ServerManager.RecvBytes(out byte[] fileContent);

				File.WriteAllBytes(modsPath + "\\" + buff, fileContent);

                SessionModel.ServerManager.SendString("OK");
			}
		}

		private void RecieveConfig()
		{
            SessionModel.ServerManager.SendString("GetConfig");
            SessionModel.ServerManager.RecvString(out string buff);

            if (buff != "OK")
            {
                LogsController.Info("Skipping config synchronization, disabled by the server");
                return;
            }

            LogsController.Info("Downloading config");

            string configPath = Settings.MinecraftPath + "\\config";

            if (Directory.Exists(configPath))
                Directory.Delete(configPath, true);

            Directory.CreateDirectory(configPath);

            SessionModel.ServerManager.RecvBytes(out byte[] zipFile);

			string zipPath = Directory.GetCurrentDirectory() + "\\config.zip";

            File.WriteAllBytes(zipPath, zipFile);

			ZipFile.ExtractToDirectory(zipPath, configPath);

			File.Delete(zipPath);
        }

		private void RecieveForge()
		{
            SessionModel.ServerManager.SendString("GetForge");
            SessionModel.ServerManager.RecvString(out string buff);

            if (buff != "OK")
            {
                LogsController.Info("Skipping forge synchronization, disabled by the server");
                return;
            }

            LogsController.Info("Downloading forge");

            SessionModel.ServerManager.RecvString(out string lastVersionId);
            SessionModel.ServerManager.RecvBytes(out byte[] zipFile);

            string zipPath = Directory.GetCurrentDirectory() + "\\forge.zip";

            File.WriteAllBytes(zipPath, zipFile);

            ZipFile.ExtractToDirectory(zipPath, Settings.MinecraftPath);

            File.Delete(zipPath);

			if (!File.Exists(Settings.MinecraftPath + "\\launcher_profiles.json"))
				return;

			JObject json = JObject.Parse(File.ReadAllText(Settings.MinecraftPath + "\\launcher_profiles.json"));

			string profileName = "Forge " + lastVersionId.Substring(0, lastVersionId.IndexOf("-"));

            json["profiles"][profileName]["name"] = profileName;
            json["profiles"][profileName]["type"] = "custom";
            json["profiles"][profileName]["lastVersionId"] = lastVersionId;
            json["profiles"][profileName]["icon"] = "data:image/png;base64,\\u003d";
            json["profiles"][profileName]["lastUsed"] = DateTime.Now.ToString("yyyy-MM-ddThh:mm:ss.000Z");

			File.WriteAllText(Settings.MinecraftPath + "\\launcher_profiles.json", json.ToString());
        }

		private void RecieveShaders()
		{
            SessionModel.ServerManager.SendString("GetShaders");
            SessionModel.ServerManager.RecvString(out string buff);

            if (buff != "OK")
            {
                LogsController.Info("Skipping shaders synchronization, disabled by the server");
                return;
            }

            LogsController.Info("Downloading shaders");

            string shadersPath = Settings.MinecraftPath + "\\shaderpacks";

            if (!Directory.Exists(shadersPath))
                Directory.CreateDirectory(shadersPath);

            while (true)
            {
                SessionModel.ServerManager.RecvString(out buff);

                if (buff == "END")
                    break;

                LogsController.Info("Downloading shader " + buff);

                SessionModel.ServerManager.RecvBytes(out byte[] fileContent);

                if (!File.Exists(shadersPath + "\\" + buff))
                    File.WriteAllBytes(shadersPath + "\\" + buff, fileContent);
                else
                    LogsController.Warn("Skipping shader " + buff + ", already exist");

                SessionModel.ServerManager.SendString("OK");
            }
        }

        private void RecieveKeymap()
        {
            SessionModel.ServerManager.SendString("GetKeymap");
            SessionModel.ServerManager.RecvString(out string buff);

            if (buff != "OK")
            {
                LogsController.Info("Skipping Keymap synchronization, disabled by the server");
                return;
            }

            LogsController.Info("Downloading Keymap");

            SessionModel.ServerManager.RecvString(out buff);

            string keymapPath = Settings.MinecraftPath + "\\options.txt";

            if (!File.Exists(keymapPath))
                return;

            Dictionary<string, string> optionsToMerge = JsonConvert.DeserializeObject<Dictionary<string, string>>(buff);

            MCoptionsController.MergeAndWrite(keymapPath, optionsToMerge);
        }
    }
}
